import React from "react";
import { useEffect } from "react";
import { useNavigationType, useLocation } from "react-router-dom";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Auth0ProviderWithHistory from "./auth0Provider";
import ProfileRecovery from "./pages/ProfileRecovery";
import ProfilePage from "./pages/ProfilePage";
import AccessAccount from "./pages/AccessAccount";


//Flipping pages and routes & Authentication

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = "";
        metaDescription = "";
        break;
      case "/profilepage":
        title = "";
        metaDescription = "";
        break;
      case "/profilerecovery":
        title = "";
        metaDescription = "";
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Router>
      <Auth0ProviderWithHistory>
        <Routes>
          <Route path="/" element={<AccessAccount />} />
          <Route path="/profilepage" element={<ProfilePage />} />
          <Route path="/profilerecovery" element={<ProfileRecovery />} />
        </Routes>
      </Auth0ProviderWithHistory>
    </Router>  
  );
}
export default App;

//STEPS FOR RUNNING THE FRON-END OF THE WEBSITE:

///Through the terminal navigate to the directory where you have saved the package.json
///Make sure you have the axios installed, if not run in terminal npm install axios
